import React from 'react';
import ReactDOM from 'react-dom/client';
import './style.css';


const root = ReactDOM.createRoot(document.getElementById('root'));
//nav component
function Navbar() {
  return (
    <nav>
      <h1>Perfumy</h1>
      <ul>
        <li>Home |</li>
        <li>Products |</li>
        <li>About |</li>
        <li>Contact</li>
      </ul>
    </nav>
  )
}

//search component
function Search() {
  return (
    <div className="searchbar">
      <input type="text" placeholder="Search" />
    </div>
  )
}

//product component
function Product() {
  return (
    <div class="products">

      <div class="box">

        <img src="https://plumgoodness.com/cdn/shop/files/MKD_01.jpg?v=1728452056&width=460" alt="perfume"></img>
        <p>Fogg Wood Extreme, Eau De Parfum, Long-lasting Perfume, 100ml</p>
      </div>


      <div class="box">
        <img src="https://marketplace.canva.com/EAGSec-fmik/2/0/1600w/canva-pink-elegant-special-sale-perfume-instagram-post-Uvwn1uyqzaM.jpg" alt="perfume"></img>
        <p>Fogg Wood Extreme, Eau De Parfum, Long-lasting Perfume, 100ml</p>
      </div>
      <div class="box">
        <img src="https://cdn.shopify.com/s/files/1/1445/2216/files/SaltAir_Collection_775x.progressive.jpg" alt="perfume"></img>
        <p>Designer Club-Be Spoke by Perfume Lounge Men's Perfume | Premium Long Lasting Perfume</p>
      </div>



    </div>
  )
}
//about component
function About(){
return(
  <div style={{textAlign: "center"}}>
    <div class="about" style={{marginTop:"10px",textAlign: "center"}}>
        <h1>About Us</h1>
        <p>The standard chunk of Lorem Ipsum used since the 1500s is reproduced below for those interested. Sections 1.10.32 and 1.10.33 from "de Finibus Bonorum et Malorum" by Cicero are also reproduced in their exact original form, accompanied by English versions from the 1914 translation by H. Rackham.</p>
    </div>
    </div>
)
}

//footer component
function Footer()
{
  return(
<footer>

            <div class="contact">



                <h3>Contact</h3>
                <p style="margin-top: 5px;">+91 3478631928 <br></br>
                    perfumy@gmail.com
                </p>

            </div>

           <p style="font-size: small; padding: 20px;color: grey;">
            W3Schools is optimized for learning and training. Examples might be simplified to improve reading and learning. Tutorials, references, and examples are constantly reviewed to avoid errors, but we cannot warrant full correctness of all content. While using W3Schools, you agree to have read and accepted our terms of use, cookie and privacy policy.

            Copyright 1999-2023 by Refsnes Data. All Rights Reserved.
            W3Schools is Powered by W3.CSS.
           </p>
        </footer>

  )
}

root.render(
  <div>
    <Navbar></Navbar>
    <Search></Search>
    <Product></Product>
    <About></About>

    <Footer></Footer>
  </div>
);
