import React from 'react';
import ReactDOM from 'react-dom/client';
import './style.css';
import one from "./assets./images/perfume1.jpg"
import two from './assets/images/perfume2.png'
import three from './assets/images/perfume3.png'


const root = ReactDOM.createRoot(document.getElementById('root'));
//nav component
function Navbar() {
  return (
    <nav>
      <h1>Perfumy</h1>
      <ul>
        <li>Home |</li>
        <li>Products |</li>
        <li>About |</li>
        <li>Contact</li>
      </ul>
    </nav>
  )
}

//search component
function Search() {
  return (
    <div className="searchbar">
      <input type="text" placeholder="Search" />
    </div>
  )
}

//product component
function Product() {
  return (
    <div class="products">

      <div class="box">

        <img src={one} alt="perfume"></img>
        <p>Fogg Wood Extreme, Eau De Parfum, Long-lasting Perfume, 100ml</p>
      </div>


      <div class="box">
        <img src={two}></img>
        <p>Fogg Wood Extreme, Eau De Parfum, Long-lasting Perfume, 100ml</p>
      </div>
      <div class="box">
        <img src={three}></img>
        <p>Designer Club-Be Spoke by Perfume Lounge Men's Perfume | Premium Long Lasting Perfume</p>
      </div>



    </div>
  )
}
//about component
function About(){
return(
  <div style="text-align: center;">
    <div class="about" style="margin-top: 10px; text-align: center;">
        <h1>About Us</h1>
        <p>The standard chunk of Lorem Ipsum used since the 1500s is reproduced below for those interested. Sections 1.10.32 and 1.10.33 from "de Finibus Bonorum et Malorum" by Cicero are also reproduced in their exact original form, accompanied by English versions from the 1914 translation by H. Rackham.</p>
    </div>
    </div>
)
}

root.render(
  <div>
    <Navbar></Navbar>
    <Search></Search>
    <Product></Product>
    <About></About>
  </div>
);
